// home.jsx
import React, { useState, useEffect } from 'react';
import './home.css';
import Product from './product';
import Cart from './cart';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faShoppingCart } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';

const Home = () => {
  const [cart, setCart] = useState([]);
  const [products, setProducts] = useState([]);
  const [isCartAnimated, setIsCartAnimated] = useState(false);
  const [isCartOpen, setIsCartOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = () => {
    setTimeout(() => {
      const mockProducts = [
       { id: 1, name: 'Boat', price: 10, image: 'data:image/jpeg;base64,' },
        { id: 2, name: 'Apple iphone', price: 15, image: 'data:image/jpeg;base64,' },
        { id: 3, name: 'Redmi note12', price: 30, image: 'data:image/jpeg;base64,' },
        { id: 4, name: 'oppo', price: 12, image: 'data:image/jpeg;base64,' },
        { id: 5, name: 'Product 5', price: 55, image: 'data:image/jpeg;base64,' },
        { id: 6, name: 'Relfex', price: 63, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSbTzwW1iW49czBFONa1eKvEXKvJIVHvuvXzA&usqp=CAU' },
        { id: 7, name: 'Product 7', price: 14, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTLSvpQ8TNFALfNJGeK_eRyCPF6nIybvRe5sA&usqp=CAU' },
        { id: 8, name: 'Product 8', price: 31, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRAFvAK7N4iF5GUBxET2J9dCZrFYaVRfgowVw&usqp=CAU' },
        { id: 9, name: 'Redmi', price: 15, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQlmahvB09hLHY3pbmfBnDLGx7jun55WovpLw&usqp=CAU' },
        { id: 10, name: 'Product 10', price: 99, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTzcKOzivya1lWhjAePVJWl9tHX7JoxSSfNzg&usqp=CAU' },
        { id: 11, name: 'vivo', price: 24, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSaGc6sK5hM6-wfSBF4oNg1J36o6AseLrunrA&usqp=CAU' },
        { id: 12, name: 'Product 12', price: 75, image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSIw3v3jZhG1N7HuNLgO6UKF6ktDXvl8yg_vg&usqp=CAU' },
        { id: 13, name: 'Titan', price: 63, image: 'data:image/jpeg;base64,' },
        { id: 14, name: 'samsung', price: 16, image: 'data:image/jpeg;base64,' },
        
      ]
      ;
      setProducts(mockProducts);
    }, 1000);
  };

  const addToCart = (product) => {
    const updatedCart = [...cart, product];
    setCart(updatedCart);

    // Save the updated cart to localStorage
    localStorage.setItem('cart', JSON.stringify(updatedCart));

    setIsCartAnimated(true);
    setTimeout(() => {
      setIsCartAnimated(false);
    }, 500);
  };

  const handleCartButtonClick = () => {
    setIsCartOpen(!isCartOpen);

    if (!isCartOpen) {
      navigate('/cart', { state: { cart } });
    }
  };

  return (
    <div className="container">
      <header className="header">
        <div className="title">E-Mart</div>
        <div className="search">
          <FontAwesomeIcon icon={faSearch} />
          <input type="text" placeholder="Search..." />
        </div>
        <button
          className={`cart ${isCartAnimated ? 'animated' : ''}`}
          onClick={handleCartButtonClick}
        >
          <FontAwesomeIcon icon={faShoppingCart} />
        </button>
      </header>
  
      <div className="content">
        <div className="products">
          {products.map((product) => (
            <Product key={product.id} product={product} onAddToCart={addToCart} />
          ))}
        </div>
        {isCartOpen && <Cart cart={cart} />}
      </div>
    </div>
  );
          }

export default Home;